var arr1 = [];

function steamroller(arr) {
    for (var i = 0; i < arr.length; i++) {
        if (Object.prototype.toString.call(arr[i]) == "[object Array]") {
            steamroller(arr[i]);
        } else {
            arr1.push(arr[i])
        }
    }
    return arr1;
}
module.exports = steamroller;