function unite() {
    var arr2 = [];
    var arr3 = [];
    for (var i = 0; i < arguments.length; i++) {
        for (var j = 0; j < arguments[i].length; j++) {
            arr2.push(arguments[i][j])
        }
    }
    arr2.filter(function(v, i) {
        if (arr2.indexOf(v) == i) {
            arr3.push(v);
        }
    })
    console.log(arr3)
}

module.exports = unite;